import React, { useState } from "react";
import ExpenseItem from "./ExpenseItems";
import "./Expenses.css";
import Card from "./Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [updateSelectedYear, setupdateSelectedYear] = useState("");

  const onRecieveSelectedYearHandler = (selectedYear) => {
    setupdateSelectedYear(selectedYear);
  };

  /*basically the jsx code here is recieving data from app.js via "props",
  to be specific its recieving the expenses items and their property to 
  pass them to each expenseitem component */
  return (
    <Card className="expenses">
      <ExpensesFilter onRecieveSelectedYear={onRecieveSelectedYearHandler} />
      {props.items.map((item) => (
        <ExpenseItem
          title={item.title}
          amount={item.amount}
          date={items.date}
        ></ExpenseItem>
      ))}

      <ExpenseItem
        title={props.items[1].title}
        amount={props.items[1].amount}
        date={props.items[1].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[2].title}
        amount={props.items[2].amount}
        date={props.items[2].date}
      ></ExpenseItem>
    </Card>
  );
};

export default Expenses;
