import React, { useState } from "react";
import ExpenseItem from "./ExpenseItems";
import "./Expenses.css";
import Card from "./Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [updateSelectedYear, setupdateSelectedYear] = useState("");

  const onRecieveSelectedYearHandler = (selectedYear) => {
    setupdateSelectedYear(selectedYear);
  };

  const filteredExpenses = props.items.filter((expenses) => {
    expenses.date.getFullYear().toString() === updateSelectedYear;
  });

  /*basically the jsx code here is recieving data from app.js via "props",
  to be specific its recieving the expenses items and their property to 
  pass them to each expenseitem component */
  return (
    <Card className="expenses">
      <ExpensesFilter onRecieveSelectedYear={onRecieveSelectedYearHandler} />
      {filteredExpenses.map((item) => (
        <ExpenseItem
          key={item.id}
          title={item.title}
          amount={item.amount}
          date={item.date}
        ></ExpenseItem>
      ))}
    </Card>
  );
};

export default Expenses;
