import "./ExpensesFilter.css";
import "./ExpensesFilter.css";

const ExpensesFilter = (props) => {
  const [updateSelectedYear, setupdateSelectedYear] = useState("");

  const filteredYearHandler = (event) => {
    const selectedYear = event.target.value;
    setupdateSelectedYear(selectedYear);
    console.log(updateSelectedYear);
    //props.onRecieveSelectedYear(updateSelectedYear);
    console.log(updateSelectedYear);
  };

  console.log(updateSelectedYear);

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select onChange={filteredYearHandler}>
          <option value="2022">2022</option>
          <option value="2021">2021</option>
          <option value="2020">2020</option>
          <option value="2019">2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
